/*
You are given a string, s, and a list of words, words, that are all of the same length. 
Find all starting indices of substring(s) in s that is a concatenation of each word in 
words exactly once and without any intervening characters.

Example 1:

Input:
  s = "barfoothefoobarman",
  words = ["foo","bar"]
Output: [0,9]
Explanation: Substrings starting at index 0 and 9 are "barfoo" and "foobar" respectively.
The output order does not matter, returning [9,0] is fine too.

Example 2:

Input:
  s = "wordgoodstudentgoodword",
  words = ["word","student"]
Output: []
*/

#include <algorithm>
#include <iostream>
#include <sstream>
#include <string>
#include <unordered_map>
#include <vector>

using namespace std;

string vecToStr(vector<string> v) {
    stringstream ss;
    for (string s : v) {
        ss << s << " ";
    }
    return ss.str();
}

string wordMapToStr(unordered_map<char, vector<string>> word_map) {
    stringstream ss;
    for (auto it = word_map.begin(); it != word_map.end(); it++) {
        ss << it->first << ": {" << vecToStr(it->second) << "} ";
    }
    return ss.str();
}

class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        // First, remove empty words.
        
        vector<string> new_words;
        for (string word : words) {
            if (word.empty()) {
                continue;
            } else {
                new_words.push_back(word);
            }
        }
        if (new_words.size() < words.size()) {
            return findSubstring(s, new_words);
        }

        // Obvious Solution
        // ================
        // Iterate through s, and at each point check if s[i] is the start of any of the given words.
        // If it is, for each word in words that starts with s[i], check that s contains that word
        // at i, and that the immediately following characters are some arrangement of the remaining
        // words.
        //
        // This works but has TLE. Need a better algorithm.
        vector<int> result;
        int total_length = 0;
        for_each(words.begin(), words.end(), [&] (string word) {
            total_length += word.size();
        });
        if (s.size() < total_length) {
            return result;
        }
        unordered_map<char, vector<string>> word_map;
        for (string word : words) {
            if (word_map.count(word[0]) > 0) {
                word_map[word[0]].push_back(word);
            } else {
                word_map[word[0]] = {word};
            }
        }
        for (int i = 0; i <= s.size() - total_length; i++) {
            if (word_map.count(s[i]) > 0) {
                for (string word : word_map[s[i]]) {
                    if (isMatch(s, i, word_map, word)) {
                        result.push_back(i);
                        break;
                    }
                }
            }
        }
        return result;
    }

    bool isMatch(string s, int i, unordered_map<char, vector<string>> word_map, string without) {
        // First remove 'without' from 'word_map'.
        if (word_map[without[0]].size() > 1) {
            bool skipped = false;
            vector<string> new_words;
            for (string word : word_map[without[0]]) {
                if (word == without && !skipped) {
                    skipped = true;
                    continue;
                }
                new_words.push_back(word);
            }
            word_map[without[0]] = new_words;
        } else {
            word_map.erase(without[0]);
        }

        for (int j = 0; j < without.size(); j++) {
            if (s[i + j] != without[j]) {
                return false;
            }
        }

        if (word_map.empty()) {
            return true;
        } else {
            char next = s[i + without.size()];
            if (word_map.count(next) > 0) {
                bool result = false;
                for (string word : word_map[next]) {
                    result |= isMatch(s, i + without.size(), word_map, word);
                }
                return result;
            } else {
                return false;
            }
        }
    }
};

string vecToStr(vector<int> v) {
    stringstream ss;
    for (int i : v) {
        ss << i << " ";
    }
    return ss.str();
}

